import React, { useContext, useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import {Button, Dropdown, Form, Col,Row, Card, Container} from "react-bootstrap";
import { Modal } from 'react-bootstrap';
import {Context} from "../index";
import '../css.css';
import { fetchProducts, fetchProportions } from '../http/productAPI';
import { fetchRecipes } from '../http/recipeAPI';
import { fetchTypes } from '../http/typeAPI';
import { fetchUser } from '../http/userAPI';


const CreateRecipe = ({show, onHide}) => {
    const {recipe} = useContext(Context)
   
    const [info, setInfo] = useState([])

    const addInfo = () => {
        setInfo([...info, {kolvo:'', productId:'', number: Date.now()}])
    }
    const removeInfo = (number) => {
        setInfo(info.filter(i => i.number !== number))
    }
    const changeInfo = (key, value, number) => {
        setInfo(info.map(i => i.number === number ? {...i, [key]: value} : i))
    }
    //const kcal = 0
    useEffect(() => {
        fetchProducts().then(data => recipe.setProducts(data))
        fetchTypes().then(data => recipe.setTypes(data))
        //fetchUser().then(data => recipe.setUser(data))
    }, [recipe])

    const [name, setName] = useState('')
    const [typeId, setTypeId] = useState('')
    const [complex, setComplex] = useState('')
    const [time, setTime] = useState('')
    const [profile_mini, setProfile_mini] = useState('')
    const [profile, setProfile] = useState('')
    const [kcal, setKcal] = useState('')
    const [protein, setProtein] = useState('')
    const [carb, setCarb] = useState('')
    const [fat, setFat] = useState('')
    const [date_rec, setDate_rec] = useState(new Date().toISOString())
    const [date_create, setDate_create] = useState(new Date().toISOString())



    return (
        
        <Modal 
        show={show}
        onHide={onHide}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered>
        <Container className="form">
        <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
                Добавление рецепта
            </Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form>
                <Form.Control value={name} onChange={e => setName(e.target.value)} placeholder={"Введите название рецепта"}/>
{/*                
                <Form.Control as="select" value={typeId} 
                onChange={e => setTypeId(e.target.value)} defaultValue="Тип блюда...">
                        {recipe.types.map(type =>
                            <option value={type.id}>{type.name}</option> 
                             
                        )}
                </Form.Control>
                
                 */}

                <Dropdown>
                        <Dropdown.Toggle>{recipe.selectedType.name || "Выберите тип"}</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {recipe.types.map(type =>
                                <Dropdown.Item
                                    onClick={() => recipe.setSelectedType(type)}
                                    key={type.id}
                                >
                                    {type.name}
                                </Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                    </Dropdown>




                <Form.Control as="select" defaultValue="Время приготовления...">
                        <option>15</option>
                        <option>30</option>
                        <option>45</option>
                        <option>60</option>  
                </Form.Control>
                
                <Form.Control as="select" defaultValue="Сложность...">
                <option>Легко</option>
                <option>Средне</option>
                <option>Сложно</option>  
                </Form.Control>
                <Form.Control className="mt-3" placeholder="Введите краткое описание рецепта"/>
                <Form.Control className="mt-3" type="multiline" placeholder="Введите рецепт"/>
                <Form.Control className="mt-3" type="file"/>
                            <hr/>
                <Button
                onClick={addInfo}
                >Добавить продукт</Button>
                {
                    info.map(i =>
                        <Row className="mt-4" key={i.number}>
                            <Col md={4}>
                            <Form.Control as="select" defaultValue="Продукт...">
                            {recipe.products.map(product =>
                                <option value={product.id} key={product.id}>{product.name}</option> 
                                  
                            )}
                            </Form.Control></Col>
                            <Col md={4}>
                            
                                <Form.Control value={i.kolvo} onChange={(e) => changeInfo('kolvo', e.target.value, i.number)} placeholder="Количество"/>
                            </Col>
                            <Col md={4}>
                                <Button onClick={() => removeInfo(i.number)} variant={"outline-danger"}>Удалить</Button>
                            </Col>
                        </Row>

                    )
                }
              
                
                 <Form.Control className="mt-3" placeholder="Каллории" />
                 <Form.Control className="mt-3" placeholder="Белки"/>
                 <Form.Control className="mt-3" placeholder="Жиры"/>
                 <Form.Control className="mt-3" placeholder="Углеводы"/>
            </Form>
        </Modal.Body>
        <Modal.Footer>
            <Button className="recbut" onClick={onHide}>Рассчитать</Button>
            <Button className="recbut" onClick={onHide}>Закрыть</Button>
            <Button className="recbut" onClick={onHide}>Добавить</Button>
        </Modal.Footer>
        </Container></Modal>
    );
  }
  
export default CreateRecipe;
