import React, {useContext} from 'react';
import {Context} from "../index";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import { NavLink } from 'react-router-dom';
import { CABINET_ROUTE, CALORIES_ROUTE, LOGIN_ROUTE, RECIPES_ROUTE, USER_ROUTE } from '../utils/consts';
import { useHistory } from 'react-router-dom';
import {Button, Container, Image, Row, Col} from "react-bootstrap";
import { observer } from 'mobx-react-lite';
import Logotip from "../assets/Logotip.png";
import "../css.css"

const NavBar = observer(() => {
    const {user} = useContext(Context)
    const history = useHistory()
    const{recipe} = useContext(Context)
    return (
        
        <Navbar variant="dark" className="navbar">
            <Container>
            <Nav className="ml-auto">
                <Button className="main" variant={"outline-light"} onClick={() => history.push(RECIPES_ROUTE)} >Главная</Button>
                <Button className="main" variant={"outline-light"} onClick={() => history.push(CALORIES_ROUTE)} >Подсчет калорий</Button>
                <Image className="logo" src={Logotip}/>
                {user.isAuth?
                <Nav className="ml-auto ">
                    <Button className="main" variant={"outline-light"} onClick={()=> history.push(CABINET_ROUTE)}>Кабинет</Button>
                    <Button className="main" variant={"outline-light"} onClick={() => user.setIsAuth(false)}>Выход</Button>
                </Nav>
                    :
                <Nav className="ml-auto">
                    <Button className="main" variant={"outline-light"} onClick={() => user.setIsAuth(true)}>Вход</Button>
                </Nav>
                }
                
            </Nav>
            <h2 className="textlogo"> Книга рецептов </h2>
        
        </Container>
        <Container className="navbar2">
            <Row md={0}>
        <Col md={5}>
        {recipe.types.map(type =>
                <Button className="type"
                    variant={"outline-light"}
                    style={{cursor:'pointer'}}
                    active={type.id === recipe.selectedType.id}
                    onClick={()=> recipe.setSelectedType(type)}
                    key={type.id}
                >
                        {type.name}
                </Button>
            )}
            </Col></Row>
        </Container>
        </Navbar>
    );
})

export default NavBar
