import React, { useContext, useState } from 'react';
import { useHistory } from 'react-router-dom';
import {Button, Form, Col,Row, Card, Container} from "react-bootstrap";
import { Modal } from 'react-bootstrap';
import {Context} from "../index";
import '../css.css';


const CreateRecipe = ({show, onHide}) => {
    const {recipe} = useContext(Context)
   
    const [info, setInfo] = useState([])

    const addInfo = () => {
        setInfo([...info, {kolvo:'', productId:'', number: Date.now()}])
    }
    const removeInfo = (number) => {
        setInfo(info.filter(i => i.number !== number))
    }
    const kcal = 0


    return (
        
        <Modal 
        show={show}
        onHide={onHide}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered>
        <Container className="form">
        <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
                Добавление рецепта
            </Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form>
                <Form.Control placeholder={"Введите название рецепта"}/>
                <Form.Control as="select" defaultValue="Тип блюда...">
                        {recipe.types.map(type =>
                            <option key={type.id}>{type.name}</option> 
                             
                        )}
                </Form.Control>
                
                
                <Form.Control as="select" defaultValue="Время приготовления...">
                        <option>15</option>
                        <option>30</option>
                        <option>45</option>
                        <option>60</option>  
                </Form.Control>
                
                <Form.Control as="select" defaultValue="Сложность...">
                <option>Легко</option>
                <option>Средне</option>
                <option>Сложно</option>  
                </Form.Control>
                <Form.Control className="mt-3" placeholder="Введите краткое описание рецепта"/>
                <Form.Control className="mt-3" type="multiline" placeholder="Введите рецепт"/>
                <Form.Control className="mt-3" type="file"/>
                            <hr/>
                <Button
                onClick={addInfo}
                >Добавить продукт</Button>
                {
                    info.map(i =>
                        <Row className="mt-4" key={i.number}>
                            <Col md={4}>
                            <Form.Control as="select" defaultValue="Продукт...">
                            {recipe.products.map(product =>
                                <option key={product.id}>{product.name}</option> 
                                  
                            )}
                            </Form.Control></Col>
                            <Col md={4}>
                            
                                <Form.Control placeholder="Количество"/>
                            </Col>
                            <Col md={4}>
                                <Button onClick={() => removeInfo(i.number)} variant={"outline-danger"}>Удалить</Button>
                            </Col>
                        </Row>

                    )
                }
              
                
                 <Form.Control className="mt-3" placeholder="Каллории" />
                 <Form.Control className="mt-3" placeholder="Белки"/>
                 <Form.Control className="mt-3" placeholder="Жиры"/>
                 <Form.Control className="mt-3" placeholder="Углеводы"/>
            </Form>
        </Modal.Body>
        <Modal.Footer>
            <Button className="recbut" onClick={kcalories}>Рассчитать</Button>
            <Button className="recbut" onClick={onHide}>Закрыть</Button>
            <Button className="recbut" onClick={onHide}>Добавить</Button>
        </Modal.Footer>
        </Container></Modal>
    );
  }
  
export default CreateRecipe;
