import React, { useContext, useEffect, useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import {Button, Form, Col,Row, Card, Container} from "react-bootstrap";
import { Modal } from 'react-bootstrap';
import {Context} from "../index";
import '../css.css';
import jwt_decode from "jwt-decode";
import {createProportion, deleteProportion, fetchProducts, fetchProportions } from '../http/productAPI';
import { fetchOneUser } from '../http/userAPI';



const CreateProportion = ({show, onHide}) => {
    
    const {id} = useParams()
    const [kolvo, setKolvo] = useState('120')
    const [recipeId, setrecipeId] = useState('')
    const [productId, setproductId] = useState('')
    const {user, recipe}=useContext(Context)
    const [users, setUsers] = useState({info:[]})
   
    useEffect(() => {
        //fetchOneUser(id).then(data => setUsers(data))
        fetchProducts().then(data => recipe.setProducts(data))
        fetchProportions().then(data => recipe.setProportions(data))
    },[])

    const addProportion = () =>{
        const formData= new FormData()
        formData.append('kolvo', parseInt(kolvo))
        formData.append('recipeId', parseInt(recipeId))
        formData.append('productId', parseInt(productId))
        createProportion(formData).then()

    }
    
  
   
    const storedToken = localStorage.getItem("token");
    let decodedData = jwt_decode(storedToken);

    return (
        
        <Modal 
        show={show}
        onHide={onHide}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered>
        <Container className="form">
        <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
                Добавление пропорций
            </Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form>
            {decodedData.id}
            <Col md={4}>
            <Form.Control as="select" key={recipeId} value={recipeId} onChange={(e) => setrecipeId(e.target.value)} defaultValue="Продукт...">
                {user.recipes.map(recipe =>{
                if(decodedData.id === parseInt(recipe.userId))
                return <option key={recipe.id} value={recipe.id}>{recipe.name}</option> 
                    })}
                    </Form.Control></Col>

                {recipeId}
                {recipe.proportions.map(proportion => {
                    if (parseInt(recipeId) === parseInt(proportion.recipeId))
                    return <p className="kolvo" key={proportion.id} value={proportion.id}>
                    {recipe.products.map(product => {
                        if (product.id === proportion.productId)
                        return <div className='lol'> <text className="prodtext" value={product.id} key={product.id}>{product.name}:&emsp;&emsp;&emsp;&emsp; {proportion.kolvo} грамм</text> 
                        &emsp;&emsp;&emsp;&emsp;<hr/>
                        </div>
                        
                            }
                        )}
                        
                        </p>
                        
                    }
                )}


                <Col md={4}>
                    <Form.Control as="select" value={productId} onChange={(e) => setproductId(e.target.value)} defaultValue="Продукт...">
                    {recipe.products.map(product =>
                        <option key={product.id} value={product.id}>{product.name}</option> 
                        )}
                    </Form.Control></Col>
                  
                    <Form.Control className="mt-3" key={kolvo} value={kolvo} onChange={e => setKolvo(e.target.value)} placeholder="Введите количество в граммах"/>

               
            </Form>
        </Modal.Body>
        <Modal.Footer>
            <Button className="recbut" onClick={onHide}>Закрыть</Button>
            <Button className="recbut" onClick={addProportion}>Добавить</Button>
        </Modal.Footer>
        </Container></Modal>
    );
  }
  
export default CreateProportion;
