import React, { useContext, useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import {Button, Dropdown, Form, Col,Row, Card, Container} from "react-bootstrap";
import { Modal } from 'react-bootstrap';
import {Context} from "../index";
import jwt_decode from "jwt-decode";
import '../css.css';
import { fetchProducts, fetchProportions } from '../http/productAPI';
import { createRecipe, fetchOneRecipe, updateRecipe } from '../http/recipeAPI';
import { fetchTypes } from '../http/typeAPI';
import { fetchUser } from '../http/userAPI';


const DeleteAndUpdateRecipe = ({idRecipe, show, onHide}) => {
    const {recipe, user} = useContext(Context)
    
    

    const selectFile = e => {
        setFile(e.target.files[0])
    }
    
    const {id} = useParams()
    //const [recipess, setRecipe] = useState({info:[]})
    const [name, setName] = useState(idRecipe.name)
    const [type, setType] = useState(idRecipe.typeId)
    const [complex, setComplex] = useState(idRecipe.complex)
    const [time, setTime] = useState(idRecipe.time)
    //const [recipeId, setrecipeId] = useState(idRecipe.id)
    const [file, setFile] = useState(null)
    const [profile_mini, setProfile_mini] = useState(idRecipe.profile_mini)
    const [profile, setProfile] = useState(idRecipe.profile)
    const [kcal, setKcal] = useState(idRecipe.kcal)
    const [protein, setProtein] = useState(idRecipe.protein)
    const [carb, setCarb] = useState(idRecipe.carb)
    const [fat, setFat] = useState(idRecipe.fat)
    //const [rate, setRate] = useState('')
    //const rate = 0
    const storedToken = localStorage.getItem("token");
    let decodedData = jwt_decode(storedToken);

    const updateRecipe = () =>{
        const formData = new FormData()
        formData.append('name', name)
        formData.append('time', time)
        formData.append('complex', complex)
        formData.append('profile_mini', profile_mini)
        formData.append('profile', profile)
        //formData.append('rating', rate)
        formData.append('img', document.getElementById('ze_best_file').files[0])
        formData.append('kcal', kcal)
        formData.append('protein', protein)
        formData.append('fat', fat)
        formData.append('carb', carb)
        formData.append('typeId', parseInt(type))
        formData.append('userId', parseInt(decodedData.id))
        // formData.append('info', JSON.stringify(info))
        updateRecipe(formData).then()

    }
    
     



    return (
        
        <Modal 
        show={show}
        onHide={onHide}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered>
        <Container className="form">
        <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
            Редактирование рецепта 
            </Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form>
            <Form.Control value={idRecipe.name} onChange={e => setName(e.target.value)} placeholder={"Введите название рецепта"}/>
               
                <Form.Control as="select" value={idRecipe.type} onChange={e => setType(e.target.value)} defaultValue="Тип блюда...">
                            {recipe.types.map(type =>
                                <option value={type.id}>{type.name}</option>
                            )}
                         </Form.Control>
                   

                        


                <Form.Control as="select" value={idRecipe.time} onChange={e => setTime(e.target.value)} defaultValue="Время приготовления...">
                        <option value='15'>15</option>
                        <option value='30'>30</option>
                        <option value='45'>45</option>
                        <option value='60'>60</option>  
                </Form.Control>
                {time}
                <Form.Control as="select" value={idRecipe.complex} onChange={e => setComplex(e.target.value)} defaultValue="Сложность...">
                <option value='легко'>Легко</option>
                <option value='средне'>Средне</option>
                <option value='сложно'>Сложно</option>  
                </Form.Control>
                               
                    <Form.Control className="mt-3" value={idRecipe.profile_mini} onChange={e => setProfile_mini(e.target.value)} placeholder="Введите краткое описание рецепта"/>
                <Form.Control className="mt-3" type="multiline" value={idRecipe.profile} onChange={e => setProfile(e.target.value)} placeholder="Введите рецепт"/>
                {/* <Form.Control className="mt-3" type="multiline" value={rate} onChange={e => setRate(e.target.value)} placeholder="Рейтинг"/> */}
                <Form.Control className="mt-3" type="file" value={file} id="ze_best_file" onChange={e => setFile(e.target.value)}/>
                            <hr/>


          
              
                 <hr/>
                 <Form.Control className="mt-3" value={idRecipe.kcal} onChange={e => setKcal(e.target.value)} placeholder="Каллории" />
                 <Form.Control className="mt-3" value={idRecipe.protein} onChange={e => setProtein(e.target.value)} placeholder="Белки"/>
                 <Form.Control className="mt-3" value={idRecipe.fat} onChange={e => setFat(e.target.value)} placeholder="Жиры"/>
                 <Form.Control className="mt-3" value={idRecipe.carb} onChange={e => setCarb(e.target.value)} placeholder="Углеводы"/>
                
            </Form>
        </Modal.Body>
        <Modal.Footer>
            <Button className="recbut" onClick={onHide}>Удалить</Button>
            <Button className="recbut" onClick={onHide}>Закрыть</Button>
            <Button className="recbut" onClick={updateRecipe}>Обновить</Button>
        </Modal.Footer>
        </Container></Modal>
    );
  }
  
export default DeleteAndUpdateRecipe;
