import React, { useContext } from 'react';
import { Col, Card, Container, Button,Image } from "react-bootstrap";
import { useHistory } from 'react-router-dom';
import star from '../assets/Layer.png'
import { RECIPE_ROUTE } from '../utils/consts';
import '../css.css'
import { deleteRecipe } from '../http/recipeAPI';
import jwt_decode from "jwt-decode";
import { Context } from '..';

const RecipeItem = ({recipe}) => {
    const history = useHistory()
    const {user}=useContext(Context)

    const storedToken = localStorage.getItem("token");
    let decodedData = jwt_decode(storedToken);

    const delRecipe = () =>{
        deleteRecipe(recipe.id).then()
    }

    return (
        
        <Col>
            <Card style={{cursor: 'pointer'}} className="recipecard">
                <Image width={208} height={150} scr={'http://localhost:5000/' + recipe.img}/>
                <div className="">
                    <h3 className="nameres">{recipe.name}</h3>
                    <div className="">
                        <div className="rate">{recipe.rating}</div>
                        {recipe.userId}
                        <Image className="imgrate" width={16} height={16} scr={star}/>
                    </div>                  
                </div>
                <div className="descript">{recipe.profile_mini}</div>
                <Button 
                variant={"outline-light"}
                onClick={() => history.push(RECIPE_ROUTE + '/' + recipe.id)}
                className="watch">Посмотреть</Button>

                {user.isAuth? (recipe.userId == parseInt(decodedData.id))?
                <div>
                <Button variant={"outline-dark"} className="ml-2" onClick={delRecipe}>Удалить</Button>
                </div> :
                    <div></div>  
                    :
                    <div></div>       
                 
                }


            </Card>
        </Col>
        
    );
};

export default RecipeItem;
