import React, {useContext} from 'react';
import {Context} from "../index";
import { Col, Card } from "react-bootstrap";
import Image from "react-bootstrap/Image";
import { useHistory } from 'react-router-dom';
import star from '../assets/star1.png'
import { RECIPE_ROUTE } from '../utils/consts';
import { observer } from 'mobx-react-lite';
import '../css.css';

const Main = observer(() => {
    const history = useHistory()
    const{recipe} = useContext(Context)
    return (
        <Container className="navbar2">
        {recipe.types.map(type =>
        <Col md={4} className={"mt-3"} onClick={() => history.push(RECIPE_ROUTE + '/' + recipe.id)}>
            <Card style={{width: 150, cursor: 'pointer'}}>
                <Image width={150} height={150} scr={recipe.img}/>
                <div className="mt-1 d-flex justify-content-between align-items-center">
                    <div>Киноа</div>
                    <div className="d-flex align-items-center">
                        <div>{recipe.rating}</div>
                        <Image width={16} height={16} scr={star}/>
                    </div>                  
                </div>
                <div>{recipe.profile_mini}</div>
            </Card>
        </Col>
        )};
        </Container>
    );
});

export default Main;
