import { Container, Row, Col, Card, Image} from "react-bootstrap";
import "../css.css";
import React, { useContext } from 'react';
import RecipeItem from "../components/RecipeItem";
import {Context} from "../index";
import { useHistory } from 'react-router-dom';
import { RECIPE_ROUTE } from "../utils/consts";
import star from '../assets/star1.png'

const Main = () => {
    const history = useHistory()
    const {recipe} = useContext(Context)
        return (
            <Col md={3} className={"mt-3"} onClick={() => history.push(RECIPE_ROUTE + '/' + recipe.id)}>
            <Card style={{width: 150, cursor: 'pointer'}}>
                <Image width={150} height={150} scr={recipe.img}/>
                <div className="mt-1 d-flex justify-content-between align-items-center">
                    <div>Киноа</div>
                    <div className="d-flex align-items-center">
                        <div>{recipe.rating}</div>
                        <Image width={16} height={16} scr={star}/>
                    </div>                  
                </div>
                <div>{recipe.profile_mini}</div>
            </Card>
        </Col>
        );
};

export default Main;
