import React, { useContext, useEffect, useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import {Button, Container, Form, Row} from "react-bootstrap"
import CreateRecipe from '../modals/CreateRecipe';
import CreateProduct from '../modals/CreateProduct';
import CreateType from '../modals/CreateType';
import {Context} from "../index";
import { observer } from 'mobx-react-lite';
import { fetchCooks, fetchFavorites, fetchOneUser, fetchRecipes, fetchUser } from '../http/userAPI';
import RecipeItem from '../components/RecipeItem';
import CreateProportion from '../modals/CreateProportion';
import DeleteAndUpdateProportion from '../modals/DeleteProportion';
import DeleteAndUpdateRecipe from '../modals/UpdateRecipe';
import DeleteRecipe from '../modals/DeleteRecipe';


const UserPage = observer(() => {
    const history = useHistory()
    const {id} = useParams()
    const [recipeVisible, setRecipeVisible] = useState(false)
    const [DelAndUpProportionVisible, setDelAndUpProportionVisible] = useState(false)
    const [DelAndUpRecipeVisible, setDelAndUpRecipeVisible] = useState(false)
    const [proportionVisible, setProportionVisible] = useState(false)
    const [productVisible, setProductVisible] = useState(false)
    const [DelRecipeVisible, setDelRecipeVisible] = useState(false)
    const [typeVisible, setTypeVisible] = useState(false)
    const {recipe} = useContext(Context)
    const {user, favorite} = useContext(Context)
    
    const [users, setUsers] = useState({info:[]})
    
    useEffect(() => {
        fetchRecipes().then(data => user.setRecipe(data.rows))
        //fetchOneRecipe(id).then(data => setRecipes(data))
        fetchOneUser(id).then(data => setUsers(data))
        fetchUser().then(data=>user.setUser(data))
        fetchFavorites().then(data=>favorite.setFavorites(data))
        fetchCooks().then(data=>user.setCooks(data))
        
    },[])

        return (          
        <Container className="tbody">

           <h1>{users.name}</h1><br/><br/>

            <Button variant={"outline-dark"} className="ml-2" onClick={() => setRecipeVisible(true)}>Добавить рецепт</Button>
            <CreateRecipe show={recipeVisible} onHide={()=> setRecipeVisible(false)}/>
            
            <Button variant={"outline-dark"} className="ml-2" onClick={() => setDelRecipeVisible(true)}>Удалить рецепт</Button>
            <DeleteRecipe show={DelRecipeVisible} onHide={()=> setDelRecipeVisible(false)}/>
            

            <Button variant={"outline-dark"} className="ml-2" onClick={() => setProportionVisible(true)}>Добавить пропорции</Button>
            <CreateProportion show={proportionVisible} onHide={()=> setProportionVisible(false)}/>

            

            <Button variant={"outline-dark"} className="ml-2" onClick={() => setProductVisible(true)}>Добавить продукт</Button>
            <CreateProduct show={productVisible} onHide={()=> setProductVisible(false)}/>

            
            <Button variant={"outline-dark"} className="ml-2" onClick={() => setDelAndUpProportionVisible(true)}>Удалить или редактировать пропорции</Button>
            <DeleteAndUpdateProportion show={DelAndUpProportionVisible} onHide={()=> setDelAndUpProportionVisible(false)}/>
            
           
            
                      <h1>Ваши рецепты</h1>
            <Row className="d-flex">
            {user.recipes.map(recipe =>{
                if(users.id === parseInt(recipe.userId))
                return <RecipeItem key={recipe.id} recipe={recipe}/>})}
            </Row>
            <h1>Рецепты, которые вам понравились</h1>
            <Row className="d-flex">
            

                {recipe.recipes.map(recipe=>{
                    {recipe.favorites.map(favorite=>{
                        if((recipe.id === parseInt(favorit.recipeId))&&(parseInt(favorit.userId) === parseInt(users.id)))
                        {
                            return <RecipeItem key={recipe.id} recipe={recipe}/>
                        }
                    })}
                })}


            {favorite.favorites.map(favorit =>{
                if(parseInt(favorit.userId) === parseInt(users.id))
                { favorite.recipes.map(recipe=>{
                    if(recipe.id === parseInt(favorit.recipeId))
                    return <RecipeItem key={recipe.id} recipe={recipe}/>})}})}
            </Row>
                    
        </Container>
        
        );
    
});

export default UserPage;
