import React, {useContext, useEffect} from 'react';
import {Context} from "../index";
import { Container, Row, Col, Image } from 'react-bootstrap';
import star from '../assets/Layer.png';
import { useHistory } from 'react-router-dom';
import { observer } from 'mobx-react-lite';
import { fetchTypes } from '../http/typeAPI';



const RecipePage = () => {
    const history = useHistory()
    // const{recipe} = useContext(Context)
    



    const recipe = {id:1, name:'Отбивные', time:45, complex:'средне', profile_mini:'Предлагаю приготовить отбивные из куриной грудки. Отбитое куриное филе панируем в сухарях с сыром, обжариваем до хрустящей корочки. Затем отбивные заливаем ароматным томатным соусом, посыпаем моцареллой и твёрдым сыром, запекаем в духовке. Получаем нежное, ароматное куриное мясо с хрустящей корочкой.', profile:'Подготавливаем необходимые продукты. В процессе приготовления понадобится пищевая плёнка или полиэтиленовый пакет. Грудку вымываем и обсушиваем бумажными полотенцами. Затем делим на две половинки и каждую разрезаем вдоль на две части. Получаем 4 кусочка куриного мяса из одной грудки или двух кусочков филе.\t Приятного аппетита!Подготавливаем необходимые продукты. В процессе приготовления понадобится пищевая плёнка или полиэтиленовый пакет. Грудку вымываем и обсушиваем бумажными полотенцами. Затем делим на две половинки и каждую разрезаем вдоль на две части. Получаем 4 кусочка куриного мяса из одной грудки или двух кусочков филе.\t Приятного аппетита!', rating:4, img:'https://img1.russianfood.com/dycontent/images_upl/498/sm_497065.jpg', userId:'1', typeId:'1'}
    useEffect(() => {
        fetchTypes().then(data => recipe.setTypes(data))
    },[])
    const proportion = [{id:1, kolvo:150, recipeId: 1, productId: 4},
        {id:2, kolvo:147, recipeId: 1, productId: 8},
        {id:3, kolvo:230, recipeId: 1, productId: 1},
        {id:4, kolvo:120, recipeId: 1, productId: 16}]
    const products=[{id:1, name:'Курица', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:2, name:'Яйцо', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:3, name:'Мука', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:4, name:'Продукт4', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:5, name:'Продукт5', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:6, name:'Продукт6', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:7, name:'Продукт7', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:8, name:'Продукт8', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:9, name:'Продукт9', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:10, name:'Продукт10', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:11, name:'Продукт11', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:12, name:'Продукт12', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:13, name:'Продукт13', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:14, name:'Продукт14', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:15, name:'Продукт15', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:16, name:'Продукт16', kcal: 340, protein: 12, fat: 7, carb: 10}]
    const recipekcals=[{id:1, kcal: 340, protein: 12, fat: 7, carb: 10, recipeId: 1},
        {id:2, kcal: 340, protein: 12, fat: 7, carb: 10, recipeId: 2}]
    //const user={id:1, name:'Петр', last_name:'Иванов', phone: '89225689674', email: 'user@dfgh.com', password:'tcfyvgu'}
        return (
            <Container
            className="tbody">
                    <div>
                    <Image className="photo" width={300} height={300} src={recipe.img}/>
                    <div className="info">
                    <h2 className="namerec1">{recipe.name}</h2>
                    <div className="raterec"> <text>{recipe.rating}</text><img width={20} height={20} src={star}/> </div>
                    <text className="descript_mini">{recipe.profile_mini}</text>
                    
                    <div className="xar">
                    <p className="textinfo">Категория
                    {recipe.recipes.map(type => {
                        if (type.id === parseInt(recipe.typeId))
                            return <text className="infotext"> {type.name}</text>
                    })}</p>
                    
                    <p className="textinfo">Время приготовления
                    <text className="infotext">{recipe.time}</text></p>
                    <div id="col2">
                    <p className="textinfo">Сложность
                    <text className="infotext" >{recipe.complex}</text></p>
                    
                    <p className="textinfo">Калории  
                    {recipekcals.map(recipekcals => {
                        if (recipe.id === parseInt(recipekcals.recipeId))
                            return <text className="infotext"> {recipekcals.kcal}</text>
                    })}</p>                    
                    </div></div></div>

                    <div className="decriptrec">
                        <p className='spisok'>Список продуктов</p>
                        <div className="product1">
                            {proportion.map(proportion => {
                                if (recipe.id === parseInt(proportion.recipeId))
                                    return <p className="kolvo" key={proportion.id}>
                                        {products.map(products => {
                                            if (products.id === proportion.productId) return <text className="prodtext" key={products.id}>{products.name}:&emsp;&emsp;&emsp;&emsp; {proportion.kolvo} грамм</text> 
                                        }
                                    )}
                                    </p>
                            }
                        )}


                        </div>
                    </div>
                    <div className="calor">
                        <p className='spisok'>Калории</p>
                            <div className="spcal">
                                {recipekcals.map(recipekcals => {
                                    if(recipe.id === parseInt(recipekcals.recipeId))
                                        return <text className=""><p>Калории &emsp;&emsp;&emsp;&emsp; {recipekcals.kcal}</p>
                            <p>Белки &emsp;&emsp;&emsp;&emsp; {recipekcals.protein}</p>
                            <p>Жиры &emsp;&emsp;&emsp;&emsp; {recipekcals.fat}</p>
                            <p>Углеводы &emsp;&emsp;&emsp;&emsp; {recipekcals.carb}</p>
                            </text>
                        
                    })}
                    </div></div>
                    <div className="step">
                    <h2 className="steps">Этапы приготовления</h2>
                    <text>{recipe.profile}</text>
                    </div>
                </div>
            <div className="prtype"></div>
            </Container>
        );
    
};

export default RecipePage;
