import React, { useContext, useEffect, useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import {Button, Container, Form, Row} from "react-bootstrap"
import CreateRecipe from '../modals/CreateRecipe';
import CreateProduct from '../modals/CreateProduct';
import CreateType from '../modals/CreateType';
import {Context} from "../index";
import { observer } from 'mobx-react-lite';
import { fetchOneUser, fetchUser } from '../http/userAPI';
import { fetchCabinet, fetchOneCabinet } from '../http/cabinetAPI';
import { fetchOneRecipe, fetchRecipes } from '../http/recipeAPI';
import RecipeItem from '../components/RecipeItem';


const UserPage = observer(() => {
    const history = useHistory()
    const {id} = useParams()
    const [recipeVisible, setRecipeVisible] = useState(false)
    
    const [productVisible, setProductVisible] = useState(false)
    const [typeVisible, setTypeVisible] = useState(false)
    const {recipe} = useContext(Context)
    const {user} = useContext(Context)
    //const [cabinets, setCabinets] = useState({info:[]})
    //const [recipes, setRecipes]=useState({info:[]})
    const [users, setUsers] = useState({info:[]})
    
    useEffect(() => {
        fetchRecipes().then(data => recipe.setRecipe(data))
        //fetchOneRecipe(id).then(data => setRecipes(data))
        fetchOneUser(id).then(data => setUsers(data))
        fetchUser().then(data=>user.setUser(data))
        //fetchOneCabinet(id).then(data=>setCabinets(data))
    },[])

        return (          
        <Container className="tbody">

            {users.name}<br/>{users.id}<br/><br/>

            <Button variant={"outline-dark"} className="ml-2" onClick={() => setRecipeVisible(true)}>Добавить рецепт</Button>
            <CreateRecipe show={recipeVisible} onHide={()=> setRecipeVisible(false)}/>
            
            <Button variant={"outline-dark"} className="ml-2" onClick={() => setProductVisible(true)}>Добавить продукт</Button>
            <CreateProduct show={productVisible} onHide={()=> setProductVisible(false)}/>

            
            <Button variant={"outline-dark"} className="ml-2" onClick={() => setTypeVisible(true)}>Добавить тип</Button>
            <CreateType show={typeVisible} onHide={()=> setTypeVisible(false)}/>
         
            
                      
            <Row className="d-flex">
            {recipe.recipes.map(recipe =>{
                //if (parseInt(recipe.userId) === parseInt(users.id))
                <RecipeItem key={recipe.userId} recipe={recipe}/>
            })}
            </Row>
                    
        </Container>
        
        );
    
});

export default UserPage;
