import React, {useContext, useState} from 'react';
import { Container, Form } from "react-bootstrap";
import { Card} from "react-bootstrap";
import { Button} from "react-bootstrap";
import { Row } from "react-bootstrap";
import {Context} from "../index";
import {NavLink, useLocation} from "react-router-dom";
import { LOGIN_ROUTE, MAIN_ROUTE, REGISTRATION_ROUTE } from "../utils/consts";
import { useHistory } from 'react-router-dom';
import {login, registration} from "../http/userAPI";
import "../css.css";

const Auth = () => {
    const history = useHistory()
    const location = useLocation()
    const {user} = useContext()
    const isLogin = location.pathname === LOGIN_ROUTE
    const [name, setName] = useState('')
    const [last_name, setLast_name] = useState('')
    const [phone, setPhone] = useState('')
    const [password, setPassword] = useState('')
    const [email, setEmail] = useState('')
    

    const click = async () => {
        try {
            let data;
            if (isLogin) {
                data = await login(email, password);
            } else {
                data = await registration(name, last_name, phone, email, password);
            }
            user.setUser(user)
            user.setIsAuth(true)
            history.push(MAIN_ROUTE)
        } catch (e) {
            alert(e.response.data.message)
        }

    }

        return (
        <Container 
            className="tbody"
            style={{height: window.innerHeight-54}}> 
            <Card style={{width:833}} className={isLogin ? "log": "reg"}>
                <h2 className = "authreg">{isLogin ? 'Авторизация': "Регистрация"}</h2>
                <Form>
                    {isLogin? 
                    <div>
                    <Form.Control
                        className="pole"
                        placeholder="Введите ваш email..."
                        id="p1"
                    />
                    <Form.Control
                        className="pole"
                        placeholder="Введите ваш пароль..."
                        id="p2"
                    />
                    </div>
                    :
                    <div>
                    <Form.Control
                        className="pole"
                        placeholder="Ваше имя..."
                        id="p1"
                        value={name}
                        onChange={e => setName(e.target.value)}
                    />
                    <Form.Control
                        className="pole"
                        placeholder="Ваша фамилия..."
                        id="p2"
                        value={last_name}
                        onChange={e => setLast_name(e.target.value)}
                    />
                    <Form.Control
                        className="pole"
                        placeholder="Номер телефона..."
                        id="p5"
                        value={phone}
                        onChange={e => setPhone(e.target.value)}
                    />
                    <Form.Control
                        className="pole"
                        placeholder="Введите ваш email..."
                        id="p6"
                        value={email}
                        onChange={e => setEmail(e.target.value)}
                    />
                    <Form.Control
                        className="pole"
                        placeholder="Введите пароль..."
                        id="p7"
                        value={password}
                        onChange={e => setPassword(e.target.value)}
                        type="password"
                    />
                    <Form.Control
                        className="pole"
                        placeholder="Повторите пароль..."
                        id="p8"
                    />
                    </div>
                    }
                    <Row className="butreg">
                        {isLogin?
                        <div>
                            Нет аккаунта? <NavLink to={REGISTRATION_ROUTE}>Создать аккаунт</NavLink>
                        </div>
                        :
                        <div>
                            Есть аккаунт? <NavLink to={LOGIN_ROUTE}>Войти</NavLink>
                        </div>
                        }
                        <Button className="vhodreg"
                        onClick={click}
                        variant={"outline-succes"}>
                            {isLogin? "Войти" : "Регистрация"}
                        </Button>
                        
                    </Row>
                    
                </Form>
            </Card>
        </Container>
    );
    
};

export default Auth;
