import React, {useContext, useEffect, useState} from 'react';
import {Context} from "../index";
import { Container, Row, Col, Image } from 'react-bootstrap';
import star from '../assets/Layer.png';
import { useHistory, useParams } from 'react-router-dom';
import { observer } from 'mobx-react-lite';
import { fetchOneRecipe } from '../http/recipeAPI';
import { fetchTypes } from '../http/typeAPI';
import { fetchRecipekcals } from '../http/productAPI';



const RecipePage = observer(() => {
    const history = useHistory()
    const [recipes, setRecipe] = useState({info:[]})
    const {recipe} = useContext(Context)
    const {id} = useParams()
    
    useEffect(()=>{
        fetchOneRecipe(id).then(data => setRecipe(data))
        fetchTypes().then(data => recipe.setTypes(data))
        fetchRecipekcals().then(data => recipe.setRecipekcals(data.rows))
    },[])

    console.log(recipe)


    const proportion = [{id:1, kolvo:150, recipeId: 1, productId: 4},
        {id:2, kolvo:147, recipeId: 1, productId: 8},
        {id:3, kolvo:230, recipeId: 1, productId: 1},
        {id:4, kolvo:120, recipeId: 1, productId: 16}]
    // const type=[]
    const products=[]
    
    //const user={id:1, name:'Петр', last_name:'Иванов', phone: '89225689674', email: 'user@dfgh.com', password:'tcfyvgu'}
        return (
            <Container
            className="tbody">
                    <div>
                    <Image className="photo" width={300} height={300} src={recipe.img}/>
                    <div className="info">
                    <h2 className="namerec1">{recipes.name}</h2>
                    <div className="raterec"> <text>{recipes.rating}</text><img width={20} height={20} src={star}/> </div>
                    <text className="descript_mini">{recipes.profile_mini}</text>
                    
                    <div className="xar">
                    <p className="textinfo">Категория
                    {recipe.types.map(items => {
                        if (items.id === parseInt(recipes.typeId))
                            return <text className="infotext"> {items.name}</text>
                    })}</p>
                    
                    <p className="textinfo">Время приготовления
                    <text className="infotext">{recipes.time}</text></p>
                    <div id="col2">
                    <p className="textinfo">Сложность
                    <text className="infotext" >{recipes.complex}</text></p>
                    
                    <p className="textinfo">Калории  
                    {recipe.recipekcals.map(recipekcals => {
                        if (recipes.id === parseInt(recipekcals.recipeId))
                            return <text className="infotext"> {recipekcals.kcal}</text>
                    })}</p>                    
                    </div></div></div>

                    <div className="decriptrec">
                        <p className='spisok'>Список продуктов</p>
                        <div className="product1">
                            {proportion.map(proportion => {
                                if (recipe.id === parseInt(proportion.recipeId))
                                    return <p className="kolvo" key={proportion.id}>
                                        {products.map(products => {
                                            if (products.id === proportion.productId) return <text className="prodtext" key={products.id}>{products.name}:&emsp;&emsp;&emsp;&emsp; {proportion.kolvo} грамм</text> 
                                        }
                                    )}
                                    </p>
                            }
                        )}


                        </div>
                    </div>
                    <div className="calor">
                        <p className='spisok'>Калории</p>
                            <div className="spcal">
                                {recipe.recipekcals.map(recipekcal => {
                                    if(recipe.id === parseInt(recipekcal.recipeId))
                                        return <text className=""><p>Калории &emsp;&emsp;&emsp;&emsp; {recipekcal.kcal}</p>
                            <p>Белки &emsp;&emsp;&emsp;&emsp; {recipekcal.protein}</p>
                            <p>Жиры &emsp;&emsp;&emsp;&emsp; {recipekcal.fat}</p>
                            <p>Углеводы &emsp;&emsp;&emsp;&emsp; {recipekcal.carb}</p>
                            </text>
                        
                    })}
                    </div></div>
                    <div className="step">
                    <h2 className="steps">Этапы приготовления</h2>
                    <text>{recipes.profile}</text>
                    </div>
                </div>
            <div className="prtype"></div>
            </Container>
        );
    
});

export default RecipePage;
