import { Container, Card, Row, Col, Button} from "react-bootstrap";
import "../css.css";
import React, { useContext } from 'react';
import RecipeItem from "../components/RecipeItem";
import {Context} from "../index";
import { useHistory } from 'react-router-dom';
import { observer } from "mobx-react-lite";
import { RECIPES_ROUTE } from "../utils/consts";


const Main = observer(() => {
    const {recipe} = useContext(Context)
    const history = useHistory()
        return (
            

            <Container
            className="tbody"> 
            
            <Container className="found">

            <Container className="time">
                <h2 className="textfound"></h2>
            </Container>

            <Container className="slozh">
                <h2 className="textfound"></h2>
            </Container>

            <Container className="kcal">
                <h2 className="textfound"></h2>
            </Container>

            </Container>

            <Container className="cards">
                <h2 className="textpop">Популярные рецепты</h2>
                <Row><Col md={9}>
            <Row className="d-flex">
            {recipe.recipes.map(recipe =>
                <div className={recipe.id <= 3? "":"type1"}>
                <RecipeItem 
                key={recipe.id} recipe={recipe}/></div>)}
            <Button className="morerec" onClick={() => history.push(RECIPES_ROUTE)}>Больше рецептов</Button>
            </Row>
            </Col> </Row>
            </Container>
            <div className="prtype"></div>
            </Container>
        );
});

export default Main;
