import {makeAutoObservable} from "mobx";

export default class RecipeStore{
    constructor() {
        this._types = []
        this._recipes = []
        this._products=[]
        this._proportions=[]
        this._users=[]
        this._cabinets=[]
        //this._recipekcals=[]
        this._selectedType = {}
        this._selectedProduct = {}
        this._selectedProportion = {}
       // this._selectedRecipekcal = {}
       this._page = 1
       this._totalCount = 0
       this._limit = 3
        makeAutoObservable(this)
    }

    setTypes(types){
        this._types = types
    }
    setUser(users){
        this._users = users
    }
    setRecipe(recipes){
        this._recipes = recipes
    }
    setSelectedType(type){
        this.setPage(1)
        this._selectedType = type
    }
    setSelectedProduct(product){
        this.setPage(1)
        this._selectedProduct = product
    }
    setPage(page) {
        this._page = page
    }
    setTotalCount(count) {
        this._totalCount = count
    }
    setProducts(products){
        this._products = products
    }
    setSelectedProportoin(proportoin){
        this._selectedProportoin = proportoin
    }
    setProportions(proportions){
        this._proportions = proportions
    }
    setCabinets(cabinets){
        this._cabinets = cabinets
    }
    get cabinets(){
        return this._cabinets
    }
    get types(){
        return this._types
    }
    get users(){
        return this._users
    }
    get recipes(){
        return this._recipes
    }
    get selectedType(){
        return this._selectedType
    }
    get products(){
        return this._products
    }
    get selectedProduct(){
        return this._selectedProduct
    }
    // get recipekcals(){
    //      return this._recipekcals
    // }
    // get selectedRecipekcal(){
    //     return this._selectedRecipekcal
    // }
    get proportions(){
        return this._proportions
    }
    get selectedProportoin(){
        return this._selectedProportion
    }
}
