import React from 'react';
import { Col, Card, Container, Button } from "react-bootstrap";
import Image from "react-bootstrap/Image";
import { useHistory } from 'react-router-dom';
import star from '../assets/star1.png'
import { RECIPE_ROUTE } from '../utils/consts';
import '../css.css'

const RecipeItem = ({recipe}) => {
    const history = useHistory()
    return (
        
        <Col onClick={() => history.push(RECIPE_ROUTE + '/' + recipe.id)} >
            <Card style={{cursor: 'pointer'}} className="recipecard">
                <Image width={150} height={150} scr={recipe.img}/>
                <div className="mt-1 d-flex justify-content-between align-items-center">
                    <div>{recipe.name}</div>
                    <div className="d-flex align-items-center">
                        <div>{recipe.rating}</div>
                        <Image width={16} height={16} scr={star}/>
                    </div>                  
                </div>
                <div>{recipe.profile_mini}</div>
                <Button 
                onClick={() => history.push(RECIPE_ROUTE + '/' + recipe.id)}
                className="btn">Посмотреть</Button>
            </Card>
        </Col>
        
    );
};

export default RecipeItem;
