import React, {useContext} from 'react';
import {Context} from "../index";
import { Container, Col, Image } from 'react-bootstrap';
import star from '../assets/Layer.png';
import { useHistory } from 'react-router-dom';
import { observer } from 'mobx-react-lite';

const RecipePage = () => {
    const history = useHistory()
    const recipe = {id:1, name:'Отбивные', time:15, complex:'легко', profile_mini:'веснмпгир', profole:'сми', rating:4, img:'https://img1.russianfood.com/dycontent/images_upl/285/big_284736.jpg', userId:'1', typeId:'3'}
    const proportion = [{id:1, kolvo:150, recipeId: 1, productId: 4},
        {id:2, kolvo:147, recipeId: 1, productId: 8},
        {id:3, kolvo:230, recipeId: 1, productId: 1},
        {id:4, kolvo:120, recipeId: 1, productId: 16}]
    const type=[{id:1, name:'Выпечка'},
    {id:2, name:'Закуски'},
    {id:3, name:'Супы'},
    {id:4, name:'Десерты'},
    {id:5, name:'Выпечка1'}]
    const products=[{id:1, name:'Свинина', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:2, name:'Яйцо', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:3, name:'Мука', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:4, name:'Продукт4', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:5, name:'Продукт5', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:6, name:'Продукт6', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:7, name:'Продукт7', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:8, name:'Продукт8', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:9, name:'Продукт9', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:10, name:'Продукт10', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:11, name:'Продукт11', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:12, name:'Продукт12', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:13, name:'Продукт13', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:14, name:'Продукт14', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:15, name:'Продукт15', kcal: 340, protein: 12, fat: 7, carb: 10},
    {id:16, name:'Продукт16', kcal: 340, protein: 12, fat: 7, carb: 10}]
    const recipekcals=[{id:1, kcal: 340, protein: 12, fat: 7, carb: 10, recipeId: 1},
        {id:2, kcal: 340, protein: 12, fat: 7, carb: 10, recipeId: 2}]
        return (
            <Container
            className="tbody">
                
                    <Image width={300} height={300} src={recipe.img}/>
                    <h2 className="namerec">{recipe.name}</h2>
                    <div> <text className="descript_mini">{recipe.rating}</text><img src={star}/> </div>
                    <text className="descript_mini">{recipe.profile_mini}</text>
                    <div className="info">

                    <p className="textinfo">Категория
                    {types.map(type => {
                        if (type.id === parseInt(recipe.typeId))
                            return <text className="infotext"> {type.name}</text>
                    })}</p>
                    
                    <text className="textinfo">Время приготовления</text>
                    <text className="infotext"></text>
                    
                    <text className="textinfo">Сложность</text>
                    <text className="infotext"></text>
                    
                    <text className="textinfo">Калории</text>
                    <text className="infotext"></text>
                    
                    <text className="textinfo">Автор рецепта</text>
                    <text className="infotext"></text>
                    
                    </div>
            </Container>
        );
    
};

export default RecipePage;
