import React, { useContext, useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import {Button, Form, Col,Row, Card, Container} from "react-bootstrap";
import { Modal } from 'react-bootstrap';
import {Context} from "../index";
import '../css.css';
import { createProduct, fetchProducts, fetchProportions } from '../http/productAPI';
import { fetchRecipes } from '../http/recipeAPI';
import { fetchTypes } from '../http/typeAPI';
import { fetchUser } from '../http/userAPI';


const CreateProduct = ({show, onHide}) => {
    
    const [name, setName] = useState('')
    const [kcal, setKcal] = useState('')
    const [protein, setProtein] = useState('')
    const [carb, setCarb] = useState('')
    const [fat, setFat] = useState('')
    const [date_rec, setDate_rec] = useState(new Date().toISOString())
    const [date_create, setDate_create] = useState(new Date().toISOString())

    const Create = () =>{
        try {
            createProduct({name: name, kcal:kcal, protein:protein, fat:fat,
            carb:carb, createdAt:date_rec, updatedAt:date_create}).then()
           
            alert("Данные добавлены");
           // history.push(MAIN_ROUTE)
        } catch (e) {
            return alert(e.response.data.message)
        }
    }


    return (
        
        <Modal 
        show={show}
        onHide={onHide}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered>
        <Container className="form">
        <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
                Добавление продукта
            </Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form>
                <Form.Control value={name} onChange={e => setName(e.target.value)} placeholder={"Введите название рецепта"}/>
                <Form.Control value={kcal} onChange={e => setKcal(e.target.value)} className="mt-3" placeholder="Каллории" />
                <Form.Control value={protein} onChange={e => setProtein(e.target.value)} className="mt-3" placeholder="Белки"/>
                <Form.Control value={fat} onChange={e => setFat(e.target.value)} className="mt-3" placeholder="Жиры"/>
                <Form.Control value={carb} onChange={e => setCarb(e.target.value)} className="mt-3" placeholder="Углеводы"/>
            </Form>
        </Modal.Body>
        <Modal.Footer>
            <Button className="recbut" onClick={onHide}>Закрыть</Button>
            <Button className="recbut" onClick={Create}>Добавить</Button>
        </Modal.Footer>
        </Container></Modal>
    );
  }
  
export default CreateProduct;
