import { Container, Card, Row, Col, Button} from "react-bootstrap";
import "../css.css";
import React, { useContext } from 'react';
import RecipeItem from "../components/RecipeItem";
import {Context} from "../index";
import { useHistory } from 'react-router-dom';
import { observer } from "mobx-react-lite";
import { RECIPES_ROUTE } from "../utils/consts";
import time1 from "../assets/2278023 (1).png";
import time2 from "../assets/2278021 (1).png";
import time3 from "../assets/2278019 (1).png";
import time4 from "../assets/2278024 (1).png";


const Main = observer(() => {
    const {recipe} = useContext(Context)
    const history = useHistory()
        return (
            

            <Container
            className="tbody"> 
            
            <Container className="found">

            <Container className="time">
                <h2 className="textfound">Время приготовления</h2>
                <Button className="buttime"><img src={time3} className="imgtime"/>
                    15 минут
                </Button>

                <Button className="buttime"><img src={time2} className="imgtime"/>
                    30 минут
                </Button>

                <Button className="buttime"><img src={time1} className="imgtime"/>
                    45 минут
                </Button>

                <Button className="buttime"><img src={time4} className="imgtime"/>
                    60 минут
                </Button>

            </Container>

            <Container className="slozh">
                <h2 className="textfound">Сложность</h2>
                <Button className="butslozh">Легко</Button>
                <Button className="butslozh">Средне</Button>
                <Button className="butslozh">Сложно</Button>
            </Container>

            <Container className="kcal">
                <h2 className="textfound">Калорийность</h2>
            </Container>

            </Container>

            <Container className="cards">
                <h2 className="textpop">Популярные рецепты</h2>
                <Row><Col md={9}>
            <Row className="d-flex">
            {recipe.recipes.map(recipe =>
                <div className={recipe.id <= 3? "":"type1"}>
                <RecipeItem 
                key={recipe.id} recipe={recipe}/></div>)}
            <Button className="morerec" onClick={() => history.push(RECIPES_ROUTE)}>Больше рецептов</Button>
            </Row>
            </Col> </Row>
            </Container>
            <div className="prtype"></div>
            </Container>
        );
});

export default Main;
