import {makeAutoObservable} from "mobx";

export default class RecipeStore{
    constructor() {
        this._types = []
        this._recipes = []
        this._products=[]
        this._proportions=[
            {id:1, kolvo:150, recipeId: 1, productId: 4},
            {id:2, kolvo:147, recipeId: 1, productId: 8},
            {id:3, kolvo:230, recipeId: 1, productId: 1},
            {id:4, kolvo:120, recipeId: 1, productId: 16},
        ]
        this._recipekcals=[
            {id:1, kcal: 340, protein: 12, fat: 7, carb: 10, recipeId: 1},
            {id:2, kcal: 340, protein: 12, fat: 7, carb: 10, recipeId: 2},
            {id:3, kcal: 340, protein: 12, fat: 7, carb: 10, recipeId: 3},
            {id:4, kcal: 340, protein: 12, fat: 7, carb: 10, recipeId: 4},
        ]
        this._selectedType = {}
        this._selectedProduct = {}
        this._selectedProportion = {}
        this._selectedRecipekcal = {}
        makeAutoObservable(this)
    }

    setTypes(types){
        this._types = types
    }
    setRecipe(recipes){
        this._recipes = recipes
    }
    setSelectedType(type){
        this._selectedType = type
    }
    setSelectedProduct(product){
        this._selectedProduct = product
    }
    setProducts(products){
        this._products = products
    }
    setSelectedProportoin(proportoin){
        this._selectedProportoin = proportoin
    }
    setProportions(proportions){
        this._proportions = proportions
    }
    setSelectedRecipekcal(recipekcal){
        this._selectedRecipekcal = recipekcal
    }
    setRecipekcals(recipekcals){
        this._recipekcals = recipekcals
    }
    get types(){
        return this._types
    }
    get recipes(){
        return this._recipes
    }
    get selectedType(){
        return this._selectedType
    }
    get products(){
        return this._products
    }
    get selectedProduct(){
        return this._selectedProduct
    }
    get recipekcals(){
        return this._recipekcals
    }
    get selectedRecipekcal(){
        return this._selectedRecipekcal
    }
    get proportions(){
        return this._proportions
    }
    get selectedProportoin(){
        return this._selectedProportion
    }
}
