import {$authHost, $host} from "./index";


export const fetchProducts = async () => {
    const {data} = await $host.get('api/product/')
    return data
}

export const fetchProportions = async () => {
    const {data} = await $host.get('api/proportion/')
    return data
}

export const createProduct = async (product) =>{
    let prod = new FormData()
    prod.append("name", product.name)
    prod.append("kcal", product.kcal)
    prod.append("protein", product.protein)
    prod.append("fat", product.fat)
    prod.append("carb", product.carb)
    prod.append('createdAt', product.createdAt)
    prod.append('updatedAt', product.updatedAt)
    const {data} = await $authHost.post('api/product/', prod, {headers:{'Content-Type':"multipart/form-data"}})
    return data
}
