import React, {useContext} from 'react';
import {Context} from "../index";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import { NavLink } from 'react-router-dom';
import { CABINET_ROUTE, LOGIN_ROUTE, RECIPES_ROUTE, USER_ROUTE } from '../utils/consts';
import { useHistory } from 'react-router-dom';
import {Button, Container} from "react-bootstrap"
import { observer } from 'mobx-react-lite';

const NavBar = observer(() => {
    const {user} = useContext(Context)
    const history = useHistory()
    return (
        
        <Navbar bg="dark" variant="dark">
            <Container>
            <NavLink to = {RECIPES_ROUTE}> Вкусняшки </NavLink>
        {user.isAuth?
        
            <Nav className="ml-auto">
                <Button 
                variant={"outline-light"}
                onClick={()=> history.push(CABINET_ROUTE)}   
                >
                    Кабинет
                
                </Button>
                <Button variant={"outline-light"} className="ml-2" onClick={() => history.push(LOGIN_ROUTE)}>Выход</Button>
                
            </Nav>
            :
            <Nav className="ml-auto">
                <Button variant={"outline-light"} onClick={() => user.setIsAuth(true)}>Авторизация</Button>
            </Nav>
        }
        </Container>
        </Navbar>
    );
})

export default NavBar
