import React, { useContext, useEffect, useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import {Button, Form, Col,Row, Card, Container} from "react-bootstrap";
import { Modal } from 'react-bootstrap';
import {Context} from "../index";
import '../css.css';
import jwt_decode from "jwt-decode";
import { fetchFavorites } from '../http/favoriteAPI';
import { fetchRecipes } from '../http/recipeAPI';





const DeleteFavorite = ({show, onHide}) => {
    
    const [recipesId, setrecipeId] = useState('')
    const {recipe}=useContext(Context)
    const {user}=useContext(Context)
    const {favorite}=useContext(Context)
   
   
    useEffect(() => {
        fetchFavorites().then(data=>favorite.setFavorites(data.rows))
        fetchRecipes().then(data=>recipe.setRecipe(data.rows))
        //fetchRecipes().then(data=>recipe.setRecipe(data.rows))
    })

   
    const storedToken = localStorage.getItem("token");
    let decodedData = jwt_decode(storedToken);

    return (
        
        <Modal 
        show={show}
        onHide={onHide}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered>
        <Container className="form">
        <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
                Удаление 
            </Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form>
            {decodedData.id}
            <Col md={4}>
            <Form.Control as="select" value={recipesId}  onChange={(e) => setrecipeId(e.target.value)} defaultValue="Продукт...">
                {favorite.favorites.map(recipe =>{
                if(parseInt(decodedData.id) === parseInt(recipe.userId))
                return <option value={recipe.id}>{recipe.recipeId}</option> 
                    })}
                    </Form.Control></Col>

               
        

                {recipesId}
                <Col md={4}>
            <Form.Control as="select" value={recipesId}  onChange={(e) => setrecipeId(e.target.value)} defaultValue="Продукт...">
                {recipe.recipes.map(recipe =>{
                
                return <option value={recipe.id}>{recipe.name}</option> 
                    })}
                    </Form.Control></Col>

            </Form>


        </Modal.Body>
        <Modal.Footer>
            <Button className="recbut" onClick={onHide}>Закрыть</Button>
            <Button className="recbut" >Удалить</Button>
        </Modal.Footer>
        </Container></Modal>
    );
  }
  
export default DeleteFavorite;
