import React, { useContext, useState } from 'react';
import {Button, Form, Container} from "react-bootstrap";
import { Modal } from 'react-bootstrap';
import {Context} from "../index";
import jwt_decode from "jwt-decode";
import '../css.css';
import { createRecipe } from '../http/recipeAPI';



const CreateRecipe = ({show, onHide}) => {
    const {recipe} = useContext(Context)
   
    
    // const selectFile = e => {
    //     setFile(e.target.files[0])
    // }
    
   
    const [name, setName] = useState('')
    const [type, setType] = useState('')
    const [complex, setComplex] = useState('')
    const [time, setTime] = useState('')
    const [file, setFile] = useState(null)
    const [profile_mini, setProfile_mini] = useState('')
    const [profile, setProfile] = useState('')
    const [kcal, setKcal] = useState('')
    const [protein, setProtein] = useState('')
    const [carb, setCarb] = useState('')
    const [fat, setFat] = useState('')
    //const [rate, setRate] = useState('')
    //const rate = 0
    const storedToken = localStorage.getItem("token");
    let decodedData = jwt_decode(storedToken);

    const addRecipe = () =>{
        const formData = new FormData()
        formData.append('name', name)
        formData.append('time', time)
        formData.append('complex', complex)
        formData.append('profile_mini', profile_mini)
        formData.append('profile', profile)
        //formData.append('rating', rate)
        formData.append('img', document.getElementById('ze_best_file').files[0])
        formData.append('kcal', kcal)
        formData.append('protein', protein)
        formData.append('fat', fat)
        formData.append('carb', carb)
        formData.append('typeId', parseInt(type))
        formData.append('userId', parseInt(decodedData.id))
        // formData.append('info', JSON.stringify(info))
        createRecipe(formData).then()


    }
    



    return (
        
        <Modal 
        show={show}
        onHide={onHide}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered>
        <Container className="form">
        <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
                Добавление рецепта
            </Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form>
                <Form.Control value={this.name} key={name} onChange={e => this.setName(e.target.value)} placeholder={"Введите название рецепта"}/>
               
               

               
                <Form.Control as="select" key={type} value={type} onChange={e => setType(e.target.value)} defaultValue="Тип блюда...">
                            {recipe.types.map(type =>
                                <option key={type.id} value={type.id}>{type.name}</option>
                            )}
                         </Form.Control>
                   




                <Form.Control as="select" value={time} onChange={e => setTime(e.target.value)} defaultValue="Время приготовления...">
                        <option key='15' value='15'>15</option>
                        <option key='30' value='30'>30</option>
                        <option key='45' value='45'>45</option>
                        <option key='60' value='60'>60</option>  
                </Form.Control>
                {time}
                <Form.Control as="select" value={complex} onChange={e => setComplex(e.target.value)} defaultValue="Сложность...">
                <option key='легко' value='легко'>Легко</option>
                <option key='средне' value='средне'>Средне</option>
                <option key='сложно' value='сложно'>Сложно</option>  
                </Form.Control>
                               
                    <Form.Control className="mt-3" type="multiline" as="textarea" rows={3} value={this.profile_mini} key={profile_mini} onChange={e => this.setProfile_mini(e.target.value)} placeholder="Введите краткое описание рецепта"/>
                <Form.Control className="mt-3" type="multiline" as="textarea" rows={7} value={this.profile} key={profile} onChange={e => this.setProfile(e.target.value)} placeholder="Введите рецепт"/>
                {/* <Form.Control className="mt-3" type="multiline" value={rate} key={rate} onChange={e => setRate(e.target.value)} placeholder="Рейтинг"/> */}
                <Form.Control className="mt-3" type="file" value={file} key={profile} id="ze_best_file" onChange={e => setFile(e.target.value)}/>
                            <hr/>


          
              
                 <hr/>
                 <Form.Control className="mt-3"  value={this.kcal} key={kcal} onChange={e => this.setKcal(e.target.value)} placeholder="Каллории" />
                 <Form.Control className="mt-3" value={this.protein} key={protein} onChange={e => this.setProtein(e.target.value)} placeholder="Белки"/>
                 <Form.Control className="mt-3" value={this.fat} key={fat} onChange={e => this.setFat(e.target.value)} placeholder="Жиры"/>
                 <Form.Control className="mt-3" value={this.carb} key={carb} onChange={e => this.setCarb(e.target.value)} placeholder="Углеводы"/>
                
            </Form>
        </Modal.Body>
        <Modal.Footer>
            <Button className="recbut" onClick={onHide}>Рассчитать</Button>
            <Button className="recbut" onClick={onHide}>Закрыть</Button>
            <Button className="recbut" onClick={addRecipe}>Добавить</Button>
        </Modal.Footer>
        </Container></Modal>
    );
  }
  
export default CreateRecipe;
