import React, {useContext} from 'react';
import {Context} from "../index";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import { NavLink } from 'react-router-dom';
import { RECIPES_ROUTE } from '../utils/consts';
import {Button} from "react-bootstrap"
import { observer } from 'mobx-react-lite';

const NavBar = observer(() => {
    const {user} = useContext(Context)
    return (
        <Navbar bg="dark" variant="dark">
            <NavLink to = {RECIPES_ROUTE}> Вкусняшки </NavLink>
        {user.isAuth?
        
            <Nav className="ml-auto">
                <Button variant={"outline-light"}>Кабинет</Button>
                <Button variant={"outline-light"} className="ml-2" onClick={() => user.setIsAuth(false)}>Выход</Button>
                
            </Nav>
            :
            <Nav className="ml-auto">
                <Button variant={"outline-light"} onClick={() => user.setIsAuth(true)}>Авторизация</Button>
            </Nav>
        }
        </Navbar>
    );
})

export default NavBar
