import { observer } from 'mobx-react-lite';
import React, { useContext, useEffect, useState } from 'react';
import { Container, Form} from "react-bootstrap";
import { Row } from "react-bootstrap";
import { Col } from "react-bootstrap";
import { useHistory } from 'react-router';
import { Context } from '../index.js';
import { ListGroup } from 'react-bootstrap';
import RecipeList from "../components/RecipeList";
import TypeBar from "../components/TypeBar";
import { fetchProducts, fetchTypes } from '../http/typeAPI';
import RecipeItem from '../components/RecipeItem.js';

const Recipes = observer(() => {
        const{recipe} = useContext(Context)
        const [time, setTime] = useState('')
        const [complex, setComplex] = useState('')
        return (
            <Container
            className="tbody"> 
                <Row>
                <Col md={3}>
        <Form>
        <Form.Control as="select" value={time} onChange={e => setTime(e.target.value)} defaultValue="Время приготовления...">
                        <option value='15'>15</option>
                        <option value='30'>30</option>
                        <option value='45'>45</option>
                        <option value='60'>60</option>  
                </Form.Control></Form>
        
        
        {time} 
        <Form>
        <Form.Control as="select" value={complex} onChange={e => setComplex(e.target.value)} defaultValue="Время приготовления...">
                        <option value='легко'>Легко</option>
                        <option value='средне'>Средне</option>
                        <option value='сложно'>Сложно</option>
                        
                </Form.Control></Form>
        
        
        {complex} 
                    </Col>
                    <Col md={9}>
                        
                    <RecipeList/>
                    
                    </Col>
                   
                    
                </Row>
            </Container>
        );
});

export default Recipes;
