import { observer } from 'mobx-react-lite';
import React, { useContext, useEffect, useState } from 'react';
import { Container, Form} from "react-bootstrap";
import { Row } from "react-bootstrap";
import { Col } from "react-bootstrap";
import { Context } from '..';
import RecipeList from "../components/RecipeList";
import { fetchRecipes, fetchTypes } from '../http/recipeAPI';
import Pages from "../components/Pages";



const Recipes = observer(() => {
        const [time, setTime] = useState('')
        const [complex, setComplex] = useState('')
        const {recipe}=useContext(Context)
        useEffect(()=>{
                fetchTypes().then(data=>recipe.setTypes(data))
                fetchRecipes(null, 1, 2).then(data=> {
                        recipe.setRecipe(data.rows)
                        recipe.setTotalCount(data.count)
                })
        },[])
        useEffect(()=>{
                fetchRecipes(recipe.selectedType.id, recipe.page, 2).then(data=> {
                        recipe.setRecipe(data.rows)
                        recipe.setTotalCount(data.count)
                })
        },[recipe.page, recipe.selectedType])
        return (
            
            <Container
            className="tbody"> 
                <Row>
                <Col md={3}>
        <Form>
        <Form.Control as="select" value={time} onChange={e => setTime(e.target.value)} defaultValue="Время приготовления...">
                        <option value='15'>15</option>
                        <option value='30'>30</option>
                        <option value='45'>45</option>
                        <option value='60'>60</option>  
                </Form.Control></Form>
        
        
        {time} 
        <Form>
        <Form.Control as="select" value={complex} onChange={e => setComplex(e.target.value)} defaultValue="Время приготовления...">
                        <option value='легко'>Легко</option>
                        <option value='средне'>Средне</option>
                        <option value='сложно'>Сложно</option>
                        
                </Form.Control></Form>
        
        
        {complex} 
                    </Col>
                    <Col md={9}>
                        
                    <RecipeList/>
                    
                    </Col>
                   <Pages/>
                    
                </Row><div className="prtype"></div>
            </Container>
           
        );
});

export default Recipes;
