import React, {useContext} from 'react';
import {Context} from "../index";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import { NavLink } from 'react-router-dom';
import { CABINET_ROUTE, LOGIN_ROUTE, RECIPES_ROUTE, USER_ROUTE } from '../utils/consts';
import { useHistory } from 'react-router-dom';
import {Button, Container, Image} from "react-bootstrap";
import { observer } from 'mobx-react-lite';
import Logotip from "../assets/Logotip.png";
import "../css.css"

const NavBar = observer(() => {
    const {user} = useContext(Context)
    const history = useHistory()
    return (
        
        <Navbar variant="dark" className="navbar">
            <Container>
            <Image className="logo" src={Logotip}/>
            <Nav>
                <Button className="main" onClick={() => history.push(RECIPES_ROUTE)}>Главная</Button>
            </Nav>
            <h2 className="textlogo"> Книга рецептов </h2>
        {user.isAuth?

            <Nav className="ml-auto">

                <Button 
                variant={"outline-light"}
                onClick={()=> history.push(CABINET_ROUTE)}   
                >
                    Кабинет
                
                </Button>
                <Button variant={"outline-light"} className="ml-2" onClick={() => history.push(LOGIN_ROUTE)}>Выход</Button>
                
            </Nav>
            :
            <Nav className="ml-auto">
                <Button variant={"outline-light"} onClick={() => user.setIsAuth(true)}>Авторизация</Button>
            </Nav>
        }
        </Container>
        <Container className="navbar2">
            ertghyh
        </Container>
        </Navbar>
    );
})

export default NavBar
