import React, { useContext, useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import {Button, Form, Col,Row, Card, Container} from "react-bootstrap";
import { Modal } from 'react-bootstrap';
import {Context} from "../index";
import '../css.css';
import jwt_decode from "jwt-decode";
import { createProduct, deleteProportion, fetchProducts, fetchProportions } from '../http/productAPI';
import {createType} from '../http/typeAPI';
import { deleteRecipe } from '../http/recipeAPI';



const DeleteRecipe= ({show, onHide}) => {
    const storedToken = localStorage.getItem("token");
    let decodedData = jwt_decode(storedToken);
    const {user, recipe}=useContext(Context)
    const [recipeId, setRecipeId] = useState('')

    const delRecipe = () =>{
        deleteRecipe(recipeId).then()
    }

    return (
        
        <Modal 
        show={show}
        onHide={onHide}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered>
        <Container className="form">
        <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
                Удаление рецепта
            </Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form>
            <Col md={4}>
            
                {user.recipes.map(recipe =>{
                if(decodedData.id === parseInt(recipe.userId))
                return <Form.Check type="radio" key={recipe.id} value={recipe.id} onChange={(e) => setRecipeId(e.target.value)} label={`${recipe.id}  ${recipe.name}`}/> 
                    })}
                   </Col>
                
               
            </Form>
        </Modal.Body>
        <Modal.Footer>
            <Button className="recbut" onClick={onHide}>Закрыть</Button>
            <Button className="recbut" onClick={delRecipe}>Удалить</Button>
        </Modal.Footer>
        </Container></Modal>
    );
  }
  
export default DeleteRecipe;
