import React, { useContext, useEffect, useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import {Button, Form, Col,Row, Card, Container} from "react-bootstrap";
import { Modal } from 'react-bootstrap';
import {Context} from "../index";
import '../css.css';
import jwt_decode from "jwt-decode";
import { fetchFavorites } from '../http/favoriteAPI';




const DeleteFavorite = ({show, onHide}) => {
    
    const [recipesId, setrecipeId] = useState('')
    const {favorite}=useContext(Context)
   
   
    useEffect(() => {
        fetchFavorites().then(data=>favorite.setFavorites(data.rows))
    })

   
    const storedToken = localStorage.getItem("token");
    let decodedData = jwt_decode(storedToken);

    return (
        
        <Modal 
        show={show}
        onHide={onHide}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered>
        <Container className="form">
        <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
                Удаление 
            </Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form>
            {decodedData.id}
            <Col md={4}>
            <Form.Control as="select" value={recipesId}  onChange={(e) => setrecipeId(e.target.value)} defaultValue="Продукт...">
                {favorite.favorites.map(recipe =>{
                if(parseInt(decodedData.id) === parseInt(recipe.userId))
                {
                    favorite.recipes.map(recip=>{
                        if(parseInt(recip.id) === parseInt(recipe.recipeId))
                        {
                            return <option value={recip.id}>{recip.name}</option> 
                        }
                    })
                }
                
                    })}
                    </Form.Control></Col>

                {recipesId}
               
            </Form>
        </Modal.Body>
        <Modal.Footer>
            <Button className="recbut" onClick={onHide}>Закрыть</Button>
            <Button className="recbut" >Удалить</Button>
        </Modal.Footer>
        </Container></Modal>
    );
  }
  
export default DeleteFavorite;
